import React, { Component } from 'react'

class TodoList extends Component {
  // componentDidUpdate() {
  //   this.props.inputElement.current.focus()
  // }
  constructor(props){}
  createTasks = item => {
    console.log('this.state', this.props.state);
    return (
      <li className="done list-group-item">
      <input type="checkbox"  className={this.state ? 'strike': null} 
                onClick={() => this.props.addActiveClass(this)}/>{item.text}
      <button className="close-btn" key={item.key} onClick = {() => this.props.deleteItem(item.key)}>X
      </button>
      </li>
    )
  }
  render() {
    const todoEntries = this.props.entries
    const listItems = todoEntries.map(this.createTasks)
    return (
      <div className="todoListMain">
        <div className="header">
          <form onSubmit={this.props.addItem}>
            <h1 className="text-align">My To Do List</h1>
            <input
              placeholder="Title..."
              ref={this.props.inputElement}
              value={this.props.currentItem.text}
              onChange={this.props.handleInput}
            />
            <button type="submit"> Add</button>
          </form>
        </div>
        <div>
          <ul className="theList">
          {listItems}
              {/* <i className="fa fa-check"></i>{listItems}<button className="close-btn">X</button> */}
          </ul>
        </div>

      </div>
    )
  }
}

export default TodoList
